function approveFile(a,c){var b=$("#approval_comment").val();$.post("include/approval.php?approve",{file_id:a,comment:b},function(d){if(d.length>0){alert(d)}else{$("#approval-container").addClass("approved");$("#approval-container").html("<h3>Approved</h3><p>Approved by "+c+" just now"+(b.length>0?"<p><b>"+b+"</b>":""))}})}function rejectFile(a,c){var b=$("#approval_comment").val();$.post("include/approval.php?reject",{file_id:a,comment:b},function(d){if(d.length>0){alert(d)}else{$("#approval-container").addClass("rejected");$("#approval-container").html("<h3>Rejected</h3><p>Rejected by "+c+" just now"+(b.length>0?"<p><b>"+b+"</b>":""))}})};